// Input: [{"output":"```json\n[\n  {\n    \"Word\": \"Hi\",\n    \"Korean\": \"ÏïàÎÖï\",\n    \"Phonetic\": \"Annyeong\"\n  }\n]\n```"}]

const raw = $json.output || "";

// 1. Remove code fences and extra text
const cleaned = raw
  .replace(/```json/g, "")
  .replace(/```/g, "")
  .trim();

// 2. Parse to object
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  parsed = []; // fallback if invalid
}

return parsed;

